class User extends BaseUser
{
    /**
     * @var integer $id
     */
    protected $id;

    /**
     * @var Collection
     * @ORM\OneToMany(targetEntity="CG\MainBundle\Entity\Phone", mappedBy="user")
     */
    protected $phones;

    /**Â²
     * @var Collection
     * @ORM\OneToMany(targetEntity="CG\MainBundle\Entity\Computer", mappedBy="user")
     */
    protected $computers;

    /**
     * Get id
     *
     * @return integer $id
     */
    public function getId()
    {
        return $this->id;
    }

    /**
     * Constructor
     */
    public function __construct()
    {
                parent::__construct();

        $this->phones = new ArrayCollection();
        $this->computers = new ArrayCollection();
    }

   
    /**
     * Add phone
     *
     * @param \CG\MainBundle\Entity\Phone $phone
     * @return User
     */
    public function addPhone(\CG\MainBundle\Entity\Phone $phone)
    {
        $this->phones[] = $phone;

        return $this;
    }

    /**
     * Remove phone
     *
     * @param \CG\MainBundle\Entity\Phone $phone
     */
    public function removePhone(\CG\MainBundle\Entity\Phone $phone)
    {
        $this->phones->removeElement($phone);
    }

    /**
     * Get phones
     *
     * @return \Doctrine\Common\Collections\Collection 
     */
    public function getPhones()
    {
        return $this->phones;
    }
    /**
     * Add computer
     *
     * @param \CG\MainBundle\Entity\Phone $computer
     * @return User
     */
    public function addComputer(\CG\MainBundle\Entity\Computer $computer)
    {
        $this->computers[] = $computer;

        return $this;
    }

    /**
     * Remove computer
     *
     * @param \CG\MainBundle\Entity\Phone $computer
     */
    public function removeComputer(\CG\MainBundle\Entity\Computer $computer)
    {
        $this->computers->removeElement($computer);
    }

    /**
     * Get phones
     *
     * @return \Doctrine\Common\Collections\Collection 
     */
    public function getComputers()
    {
        return $this->computers;
    }
}
